mapper.select:
SELECT a.`consumer_id` AS instituteId, a.`consumer_name` AS instituteName, CONCAT(DATE_FORMAT(MIN(a.`bill_date`), '%Y-%m-%d'), ' - ', DATE_FORMAT(MAX(a.`bill_date`), '%Y-%m-%d')) AS billRegion, SUM(a.`total_amount`) AS totalAmount, SUM(a.`payment_amount`) AS paymentAmount, SUM(a.`total_amount`) - SUM(a.`payment_amount`) AS debtAmount, SUM(a.`cost_amount`) AS costAmount, SUM(a.`total_amount`) - SUM(a.`cost_amount`) AS profitAmount, count(1) AS billCount FROM se_order a
and bill_date >= #{param.start} and bill_date <= #{param.end}
and audit_status = 'YES'
GROUP BY consumer_id ORDER BY debtAmount DESC;
