mapper.select:
		select `id`,`no`,`name`,`type`,`grade`,`phone`,`address`,`status`,`create_time`,`update_time` from data_consumer

               and id = #{id} 
               and no = #{no} 
               and name = #{name} 
               and type = #{type} 
               and grade = #{grade} 
               and phone = #{phone} 
               and address = #{address} 
               and status = #{status} 
               and create_time = #{createTime} 
               and update_time = #{updateTime} 
               and ( no like '%${searchText}%' or  name like '%${searchText}%')
               and create_time >= #{start} 
                and create_time <= #{end}  



                order by ${sort} ${order}


                order by status desc,id desc



			limit #{offset}, #{limit}

		select count(*) from data_consumer

             and id = #{id} 
