mapper.select:
		select `id`,`bill_no`,`entry_id`,`entry_name`,`entry_unit`,`entry_price`,`stock_no`,`stock_name`,`total_qty`,`entry_amount`,`discount_amount`,`discount_rate`,`purchase_fee`,`total_amount`,`remark`,`request_bill_no`,`create_time`,`update_time` from po_order_entry

			and id = #{id}
			and bill_no = #{billNo}
			and entry_id = #{entryId}
			and entry_name = #{entryName}
			and entry_unit = #{entryUnit}
			and entry_price = #{entryPrice}
			and stock_no = #{stockNo}
			and stock_name = #{stockName}
			and total_qty = #{totalQty}
			and entry_amount = #{entryAmount}
			and discount_amount = #{discountAmount}
			and discount_rate = #{discountRate}
			and purchase_fee = #{purchaseFee}
			and total_amount = #{totalAmount}
			and remark = #{remark}
			and request_bill_no = #{requestBillNo}
			and create_time = #{createTime}
			and update_time = #{updateTime}
			and bill_no in
				#{billNo}




                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from po_order_entry

	and id = #{id}
