mapper.update:
update po_order_entry
`bill_no` = #{billNo}, `entry_id` = #{entryId}, `entry_name` = #{entryName}, `entry_unit` = #{entryUnit}, `entry_price` = #{entryPrice}, `stock_no` = #{stockNo}, `stock_name` = #{stockName}, `total_qty` = #{totalQty}, `entry_amount` = #{entryAmount}, `discount_amount` = #{discountAmount}, `discount_rate` = #{discountRate}, `purchase_fee` = #{purchaseFee}, and total_amount = #{totalAmount}, `remark` = #{remark}, `request_bill_no` = #{requestBillNo}, `create_time` = #{createTime}, `update_time` = #{updateTime}
where id = #{id}

